import { useState } from "react";
import ZoneSelect from "../components/zone-select";

const provinces = [
  { id: '79', name: 'Hồ Chí Minh', code_name: 'ho_chi_minh' },
  { id: '01', name: 'Hà Nội', code_name: 'ha_noi' },
];

const districts = [
  { id: '760', name: 'Quận 1', code_name: '1', province_id: '79' },
  { id: '770', name: 'Quận 3', code_name: '3', province_id: '79' },
  { id: '773', name: 'Quận 4', code_name: '4', province_id: '79' },
  { id: '774', name: 'Quận 5', code_name: '5', province_id: '79' },
  { id: '775', name: 'Quận 6', code_name: '6', province_id: '79' },
  { id: '778', name: 'Quận 7', code_name: '7', province_id: '79' },
  { id: '776', name: 'Quận 8', code_name: '8', province_id: '79' },
  { id: '771', name: 'Quận 10', code_name: '10', province_id: '79' },
  { id: '772', name: 'Quận 11', code_name: '11', province_id: '79' },
  { id: '761', name: 'Quận 12', code_name: '12', province_id: '79' },
  { id: '002', name: 'Q. Hoàn Kiếm', code_name: 'hoan_kiem', province_id: '01' },
  { id: '008', name: 'Q. Hoàng Mai', code_name: 'hoang_mai', province_id: '01' },
]

const wards = [
  { id: '760', name: 'Phường 1', code_name: '1', district_id: '760' },
  { id: '770', name: 'Phường 3', code_name: '3', district_id: '770' },
  { id: '773', name: 'Phường 4', code_name: '4', district_id: '773' },
]

export function ZoneSelectDemo() {
  const [selectedProvince, setSelectedProvince] = useState<string>("");
  const [selectedDistrict, setSelectedDistrict] = useState<string>("");
  const [selectedWard, setSelectedWard] = useState<string>("");

  const filteredDistricts = districts.filter(
    (district) => district.province_id === selectedProvince
  );

  const filteredWards = wards.filter(
    (ward) => ward.district_id === selectedDistrict
  );

  return (<div className="flex flex-col gap-4">
    <ZoneSelect 
      zone={provinces} 
      label="Province" 
      placeholder="Select province" 
      value={selectedProvince}
      onSelect={(value) => {
        setSelectedProvince(value);
        setSelectedDistrict(""); // Reset district when province changes
        setSelectedWard(""); // Reset ward when province changes
      }}
      className="w-[200px]"
    />
    <ZoneSelect 
      zone={filteredDistricts} 
      label="District" 
      placeholder="Select district" 
      value={selectedDistrict}
      disabled={!selectedProvince}
      onSelect={(value) => {
        setSelectedDistrict(value);
        setSelectedWard(""); // Reset ward when district changes
      }}
      className="w-[200px]"
    />
    <ZoneSelect 
      zone={filteredWards} 
      label="Ward" 
      placeholder="Select ward" 
      value={selectedWard}
      disabled={!selectedDistrict}
      onSelect={setSelectedWard}
      className="w-[200px]"
    />
  </div>)
}
